CREATE TABLE IF NOT EXISTS Framework(Framework_ID TEXT PRIMARY KEY,Name TEXT,CPU INTEGER,Memory REAL,Active INTEGER DEFAULT FALSE NOT NULL,Scheduled_Tasks INTEGER DEFAULT FALSE,TimeStamp TIMESTAMP NOT NULL);
CREATE TABLE IF NOT EXISTS Slave(Slave_ID TEXT PRIMARY KEY,Load_5min REAL,Free_Memory REAL,Total_Memory REAL,CPU INTEGER,Allocated_CPU REAL,IP TEXT NOT NULL,Hostname TEXT,TimeStamp TIMESTAMP NOT NULL);
CREATE TABLE IF NOT EXISTS Runs_On(Framework_ID TEXT,Slave_ID TEXT,FOREIGN KEY (Slave_ID) REFERENCES Slave (Slave_ID) ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED,FOREIGN KEY (Framework_ID) REFERENCES Framework (Framework_ID) ON DELETE CASCADE DEFERRABLE INITIALLY DEFERRED);
